package SubsetNSubsequence;

import java.util.HashMap;

public class SearchSubSeq {
    public static void main(String[] args){
//        String A="zgms";
//        String B="";
        String A="hrgguezqjsymhqa";
        String B="";
        String found = isPresent(A,B);
        System.out.println(found);
        }

    private static String isPresent(String A, String B) {
        for(char c : A.toCharArray()) {
            if (!B.contains(String.valueOf(c))) {
                return "false";
            }
        }
        int last_index = B.indexOf(A.charAt(0));
        for(int i=1; i<A.length(); i++){
           int index= B.indexOf(A.charAt(i),last_index+1);
           if(index > last_index)
               last_index=index;

           else return "false";
        }
        return "true";
    }
}
